ine[{C[[6]],C[[1]]}]} ]]Substitutions[TileList_]:= Join @@ SubstFor /@ TileListRepeatedSubst[InitialTile_,ToDepth_]:=Nest[Substitutions, {InitialTile},ToDepth] PolyTiling[InitialTile_,ToDepth_]:=PolyTile /@  		RepeatedSubst[InitialTile,ToDepth]InitTile={1,{1,1},{0,0},{0,1}}ToDepth=4Show[Graphics[Join[{GrayLevel[1]},Evaluate[PolyTiling[InitTile,ToDepth]]],AspectRatio->Automatic]]SubstFor[LTile_]:=Block[{A=AtDepth[LTile]+1,Ort=LTile[[2]],		M=MainCorner[LTile],At=LTile[[4]]},Block[{AB=(Ort[[1]]+Ort[[2]])/2,		BA=(Ort[[1]]-Ort[[2]])/2}, {     				{A,Ort,M,At} ,		{A,{-Ort[[2]],Ort[[1]]},M+PowersofTwo[[A-1]]*{AB,-BA},At},		{A,{Ort[[2]],-Ort[[1]]},M+PowersofTwo[[A-1]]*{BA,AB},At},		{A,Ort,M+PowersofTwo[[A+1]]*Ort,At} 								}]]